package dev.chicle.amongsteves.gamemanager.player;

import net.md_5.bungee.api.ChatColor;
import org.bukkit.Color;

public enum PlayerColor {
    /**
     * URL: https://mineskin.org/2143298554
     */
    BLACK(
            "",
            "",
            Color.BLACK
    ),
    /**
     * URL: https://minesk.in/2055163510
     */
    BLUE(
            "",
            "",
            Color.BLUE
    ),
    /**
     * URL: https://minesk.in/1782124949
     */
    BROWN(
            "",
            "",
            Color.fromRGB(66, 40, 14)
    ),
    /**
     * URL: https://minesk.in/685432758
     */
    CYAN(
            "",
            "",
            Color.fromRGB(0, 255, 255)
    ),
    /**
     * URL: https://minesk.in/1390872971
     */
    GREEN(
            "",
            "",
            Color.GREEN
    ),
    /**
     * URL: https://minesk.in/1408077244
     */
    LIME(
            "",
            "",
            Color.LIME
    ),
    /**
     * URL: https://minesk.in/2081157620
     */
    ORANGE(
            "",
            "",
            Color.ORANGE
    ),
    /**
     * URL: https://minesk.in/88875027
     */
    PINK(
            "",
            "",
            Color.FUCHSIA
    ),
    /**
     * URL: https://minesk.in/1639568993
     */
    PURPLE(
            "",
            "",
            Color.PURPLE
    ),
    /**
     * URL: https://mineskin.org/2020075785
     */
    RED(
            "",
            "",
            Color.RED
    ),
    /**
     * URL: https://minesk.in/1195541522
     */
    WHITE(
            "",
            "",
            Color.WHITE
    ),
    /**
     * URL: https://minesk.in/258994729
     */
    YELLOW(
            "",
            "",
            Color.YELLOW
    );


    private final String texture;
    private final String signature;
    private final Color color;

    PlayerColor(String texture, String signature, Color color) {
        this.texture = texture;
        this.signature = signature;
        this.color = color;
    }

    public String getTexture() {
        return texture;
    }

    public String getSignature() {
        return signature;
    }

    public Color getColor() {
        return color;
    }

    public ChatColor getChatColor(){
        int R = getColor().getRed(), G = getColor().getGreen(), B = getColor().getBlue();
        String hex = String.format("#%02x%02x%02x", R, G, B);
        return ChatColor.of(hex);
    }
}
